         tabBar.appendChild(tabSettings);


     tableDialog.appendChild(tabBar);
  var contentDiv=document.createElement("div");
 tableDialog.appendChild(contentDiv);

  var drawCustomBar=function(){

    contentDiv.innerHTML="";
    tabSettings.style.opacity=".5";
    tabCustomRules.style.opacity="1";


    var actionBar=document.createElement("div");
    actionBar.style.display = "flex";
    actionBar.style.justifyContent = "space-between";
    actionBar.style.alignItems = "center";
    var addButton=document.createElement("img");
      actionBar.appendChild(addButton);
       addButton.style.width="20px";
       addButton.style.height="20px";
       addButton.src=addIcon;
       addButton.style.filter="invert(1)";
    	  addButton.addEventListener("mouseover", function () {
				console.log("over maximize");
				  addButton.style.textShadow = "red 2px 0 20px";
				  addButton.style.backgroundColor = "blue";
          addButton.style.cursor="pointer";

			});
			    addButton.addEventListener("mouseout", function () {
				  addButton.style.textShadow = "";
				  addButton.style.backgroundColor = "transparent";
			});

       addButton.addEventListener("click",()=>{
         extraRules[""]="";
         buildRulesTable();
       });
    var saveButton=document.createElement("button");
      actionBar.appendChild(saveButton);
      saveButton.textContent="Save the rules"

       saveButton.addEventListener("click",()=>{
       localStorage.setItem("extraRules",JSON.stringify(extraRules));

       });

    var table=document.createElement("table");
    var trHead=document.createElement("tr");
    var th1=document.createElement("th");
    th1.textContent="Value "
    var th2=document.createElement("th");
    th2.textContent="Read as";
      trHead.appendChild(th1);
      trHead.appendChild(th2);
      table.appendChild(trHead);


    var entries=Object.entries(extraRules);


    for(let entry of entries)
    {
      let row=document.createElement("tr");
      let cell1=document.createElement("td");
      let cell2=document.createElement("td");
      cell1.style.height="20px";
      cell2.style.height="20px";
      cell1.style.minWidth="20px";
      cell2.style.minWidth="20px";
      cell1.style.border="2px solid var(--border-color)";
      cell2.style.border="2px solid var(--border-color)";
      cell1.textContent=entry[0];

      cell2.textContent=entry[1];
      cell1.addEventListener("click",()=>{

      delete extraRules[cell1.textContent];
      let inputCell=document.createElement("td");
      let input=document.createElement("input");
      input.style.width="100px";
      inputCell.appendChild(input);
      input.value= cell1.textContent;
      row.replaceChild(inputCell,cell1);

      input.addEventListener("keydown",(e)=>{
         if(e.key=="Enter")
         {
           extraRules[input.value]=cell2.textContent;
           cell1.textContent=input.value;
           row.replaceChild(cell1,inputCell);
          delete  extraRules[""]

         }


      });
      });
      cell2.addEventListener("click",()=>{
        if(cell1.textContent!=""){
      let input=document.createElement("input");
      input.style.width="100px";
      let inputCell=document.createElement("td");
      inputCell.appendChild(input);
      input.value= cell2.textContent;
      row.replaceChild(inputCell,cell2);

      input.addEventListener("keydown",(e)=>{
         if(e.key=="Enter")
         {
           extraRules[cell1.textContent]=input.value;
           cell2.textContent=input.value;
           row.replaceChild(cell2,inputCell);


         }


      })
        }
      });


      row.appendChild(cell1);
      row.appendChild(cell2);
         let closeCell=document.createElement("td")
         let closeButton=document.createElement("span");
        closeCell.appendChild( closeButton);
         closeButton.textContent="‚ùå";
         closeButton.style.marginLeft = "auto";
         closeButton.style.fontSize = "x-large";

			 closeButton.addEventListener("mouseover", function () {
				console.log("over maximize");
				 closeButton.style.textShadow = "red 2px 0 20px";
				 closeButton.style.backgroundColor = "red";
         closeButton.style.cursor="pointer";

			});
			 closeButton.addEventListener("mouseout", function () {
				 closeButton.style.textShadow = "";
				 closeButton.style.backgroundColor = "transparent";
			});



			 closeButton.addEventListener("click", function () {
       confirmPrompt(()=>
                 {

             delete extraRules[cell1.textContent];

             buildRulesTable();



           });});

         row.appendChild(closeCell);

      table.appendChild(row);
    }
   contentDiv.appendChild(actionBar);
   contentDiv.appendChild(table);

  }
